import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  decrement,
  increment,
  incrementByAmount,
  incrementAsync,
  selectCount,
} from "./../../redux/counter/counterSlice";
import styles from "./counter.module.css";

export function CounterPage() {
  const count = useSelector(selectCount);
  const dispatch = useDispatch();
  const [incrementAmount, setIncrementAmount] = useState("2");

  const incrementOnClick = () => {
    dispatch(increment());
  };
  const decrementOnClick = () => {
    dispatch(decrement());
  };
  const changeIncrementAmount = (e) => {
    setIncrementAmount(e.target.value);
  };
  const incrementByAmountOnClick = () => {
    dispatch(incrementByAmount(Number(incrementAmount) || 0));
  };
  const incrementAsyncOnClick = () => {
    dispatch(incrementAsync(Number(incrementAmount) || 0));
  };

  return (
    <div>
      <div className={styles.row}>
        <button
          className={styles.button}
          aria-label="Increment value"
          onClick={incrementOnClick}
        >
          +
        </button>
        <span className={styles.value}>{count}</span>
        <button
          className={styles.button}
          aria-label="Decrement value"
          onClick={decrementOnClick}
        >
          -
        </button>
      </div>
      <div className={styles.row}>
        <input
          className={styles.textbox}
          aria-label="Set increment amount"
          value={incrementAmount}
          onChange={changeIncrementAmount}
        />
        <button className={styles.button} onClick={incrementByAmountOnClick}>
          Add Amount
        </button>
        <button className={styles.asyncButton} onClick={incrementAsyncOnClick}>
          Add Async
        </button>
      </div>
    </div>
  );
}
